from pandas import read_excelimport matplotlib.pyplot as pltseries = read_excel('MSTAdata_33276048_33347999_33270635.xlsx',                     sheet_name='data1', header=0,  squeeze=True, dtype=float)                    #reading the basic variablesMSTA = series.MSTACH4  = series.CH4 GMAF = series.GMAFET12 = series.ET12MSTA.plot()plt.xlabel('time')plt.ylabel('Anomaly (deg C)')plt.title('MSTA')plt.show()CH4.plot()plt.xlabel('time')plt.ylabel('Monthly Atmospheric Carbon Dioxide Levels')plt.title('CH4')plt.show()GMAF.plot()plt.xlabel('time')plt.ylabel('UK  international air travel')plt.title('GMAF')plt.show()ET12.plot()plt.xlabel('time')plt.ylabel('the consumption of fuels')plt.title('ET12')plt.show()##initial view on the nature of the data set 